module.exports=class GameUpdateLoop {
    constructor(fps,callback) {
        this.fps=fps;
        this.callback=callback;
        this.tickLengthMs = 1000 / fps;
        this.previousTick = Date.now();
        this.delta;
        this.frame=0;
    }
    
    StartGameLoop(){
        var now = Date.now()
        if (this.previousTick + this.tickLengthMs <= now) {
             this.delta= (now - this.previousTick) / 1000;
            this.previousTick = now;
            this.frame++;
            this.callback(this.delta,this.frame);
        }
        if (Date.now() - this.previousTick < this.tickLengthMs - 16) {
            setTimeout(()=>this.StartGameLoop())
        } else {
            setImmediate(()=>this.StartGameLoop())
        }
    }
}